(32, 'tpl_top', '<header id=\"header\">\r\n  <div class=\"header-inner\">\r\n    <h1 id=\"logo\"><a href=\"{root_url}\">HealthyTeeth</a></h1>\r\n    <nav id=\"nav\">\r\n      {Navigator}\r\n    </nav>\r\n  </div>\r\n</header>\r\n\r\n<section id=\"banner\">\r\n  <div class=\"content\">\r\n    <header>\r\n      <h2>HealthyTeeth</h2>\r\n      <p>Witamy w najzdrowszym zakątku internetu.<br />\r\n      Profesjonalna opieka dentystyczna, której możesz zaufać.</p>\r\n    </header>\r\n    <span class=\"image\"><img src=\"uploads/images/dent-top.png\" alt=\"dentysta\" /></span>\r\n  </div>\r\n  <a href=\"#one\" class=\"goto-next scrolly\">Next</a>\r\n</section>', '', 2, NULL, NULL, 1, 1, 1747326182, 1747330338),
(33, 'tpl_mid', '<section id=\"main\" class=\"container\" style=\"padding-top: 4em; padding-bottom: 4em;\">\r\n  {content}\r\n</section>', '', 2, NULL, NULL, 1, 1, 1747326194, 1747329684),
(34, 'tpl_bottom', '<footer id=\"footer\" style=\"padding: 3em 0; text-align: center; font-size: 0.9em; color: #999;\">\r\n  <div style=\"margin-bottom: 1em;\">\r\n    <a href=\"https://www.facebook.com\" target=\"_blank\" style=\"margin: 0 0.5em; color: inherit; text-decoration: none;\">Facebook</a>\r\n    |\r\n    <a href=\"https://www.instagram.com\" target=\"_blank\" style=\"margin: 0 0.5em; color: inherit; text-decoration: none;\">Instagram</a>\r\n    |\r\n    <a href=\"mailto:kontakt@healthyteeth.pl\" style=\"margin: 0 0.5em; color: inherit; text-decoration: none;\">Email</a>\r\n  </div>\r\n  \r\n  <div style=\"opacity: 0.6;\">\r\n    &copy; HealthyTeeth {current_date format=\"%Y\"}. Wszystkie prawa zastrzeżone.\r\n  </div>\r\n  \r\n  <div style=\"margin-top: 0.5em; font-size: 0.8em; opacity: 0.4;\">\r\n    Made with ♥ by Jakub\r\n  </div>\r\n</footer>', '', 2, NULL, NULL, 1, 1, 1747326205, 1747330416),
(30, 'Main', '<!DOCTYPE html>\r\n<html lang=\"pl\">\r\n<head>\r\n  <meta charset=\"UTF-8\">\r\n  <title>{title} - {sitename}</title>\r\n  <meta name=\"viewport\" content=\"width=device-width, initial-scale=1\" />\r\n  <link rel=\"stylesheet\" href=\"uploads/landed/assets/css/main.css\" />\r\n  {metadata}\r\n  {cms_stylesheet}\r\n</head>\r\n\r\n<body class=\"is-preload landing\">\r\n  <div id=\"page-wrapper\">\r\n{include file=\'cms_template:tpl_top\'}\r\n{include file=\'cms_template:tpl_mid\'}\r\n{include file=\'cms_template:tpl_bottom\'}\r\n\r\n  </div>\r\n\r\n<!-- Scripts -->\r\n<script src=\"uploads/mine/js/jquery.min.js\"></script>\r\n<script src=\"uploads/mine/js/jquery.scrolly.min.js\"></script>\r\n<script src=\"uploads/mine/js/jquery.dropotron.min.js\"></script>\r\n<script src=\"uploads/mine/js/jquery.scrollex.min.js\"></script>\r\n<script src=\"uploads/mine/js/browser.min.js\"></script>\r\n<script src=\"uploads/mine/js/breakpoints.min.js\"></script>\r\n<script src=\"uploads/mine/js/util.js\"></script>\r\n<script src=\"uploads/mine/js/main.js\"></script>', '', 1, 1, NULL, 1, 1, 1747325154, 1747326876),
(31, 'navigation', '{* simple navigation *}\r\n{* note, function can only be defined once *}\r\n{* \r\n  variables:\r\n  node: contains the current node.\r\n  aclass: is used to build a string containing class names given to the a tag if one is used\r\n  liclass: is used to build a string containing class names given to the li tag.\r\n*}\r\n\r\n{function name=Nav_menu depth=1}{strip}\r\n<ul>\r\n  {foreach $data as $node}\r\n    {* setup classes for the anchor and list item *}\r\n    {assign var=\'liclass\' value=\'menudepth\'|cat:$depth}\r\n    {assign var=\'aclass\' value=\'\'}\r\n\r\n    {* the first child gets a special class *}\r\n    {if $node@first && $node@total > 1}{assign var=\'liclass\' value=$liclass|cat:\' first_child\'}{/if}\r\n\r\n    {* the last child gets a special class *}\r\n    {if $node@last && $node@total > 1}{assign var=\'liclass\' value=$liclass|cat:\' last_child\'}{/if}\r\n\r\n    {if $node->current}\r\n      {* this is the current page *}\r\n      {assign var=\'liclass\' value=$liclass|cat:\' menuactive\'}\r\n      {assign var=\'aclass\' value=$aclass|cat:\' menuactive\'}\r\n    {/if}\r\n\r\n    {if $node->parent}\r\n      {* this is a parent of the current page *}\r\n      {assign var=\'liclass\' value=$liclass|cat:\' menuactive menuparent\'}\r\n      {assign var=\'aclass\' value=$aclass|cat:\' menuactive menuparent\'}\r\n    {/if}\r\n\r\n    {if $node->children}\r\n      {assign var=\'liclass\' value=$liclass|cat:\' parent\'}\r\n      {assign var=\'aclass\' value=$aclass|cat:\' parent\'}\r\n    {/if}\r\n\r\n    {* build the menu item node *}\r\n    {if $node->type == \'sectionheader\'}\r\n      <li class=\'sectionheader {$liclass}\'><span>{$node->menutext}</span>\r\n        {if isset($node->children)}\r\n          {Nav_menu data=$node->children depth=$depth+1}\r\n        {/if}\r\n      </li>\r\n    {else if $node->type == \'separator\'}\r\n      <li class=\'separator {$liclass}\'><hr class=\'separator\'/></li>\r\n    {else}\r\n      {* regular item *}\r\n      <li class=\"{$liclass}\">\r\n        <a class=\"{$aclass}\" href=\"{$node->url}\"{if $node->target ne \"\"} target=\"{$node->target}\"{/if}><span>{$node->menutext}</span></a>\r\n        {if isset($node->children)}\r\n          {Nav_menu data=$node->children depth=$depth+1}\r\n        {/if}\r\n      </li>\r\n    {/if}\r\n  {/foreach}\r\n</ul>\r\n{/strip}{/function}\r\n\r\n{if isset($nodes)}\r\n{Nav_menu data=$nodes depth=0}\r\n{/if}', '', 4, 1, NULL, 1, 1, 1747325640, 1747325891);

